select `dish_list`.`id` AS `id`,`dish_list`.`phote` AS `phote`,`dish_list`.`name` AS `name`,`dish_list`.`price` AS `price`,`dish_list`.`mark` AS `mark`,`merchant_dish_relation`.`merchant_id` AS `merchant_id`,`merchant_list`.`name` AS `merchant_name` from ((`dish_list` join `merchant_list`) join `merchant_dish_relation`) where ((`merchant_list`.`id` = `merchant_dish_relation`.`merchant_id`) and (`dish_list`.`id` in (`merchant_dish_relation`.`dish_1`,`merchant_dish_relation`.`dish_2`,`merchant_dish_relation`.`dish_3`,`merchant_dish_relation`.`dish_4`,`merchant_dish_relation`.`dish_5`)))